mapper.insert:
INSERT INTO
    kidslandNotice(nNum, nCategory, nWriter, nTitle, nContent)
VALUES
    ((SELECT resultTable.resultNum FROM (SELECT IFNULL(MAX(nNum), 0) + 1 AS resultNum FROM kidslandNotice) resultTable)
    , #{nCategory}
    , #{nWriter}
    , #{nTitle}
    , #{nContent})

mapper.update:
UPDATE
    kidslandNotice
SET
    nCategory = #{nCategory}
  , nWriter = #{nWriter}
  , nTitle = #{nTitle}
  , nContent = #{nContent}
  , nDate = CURRENT_TIMESTAMP
WHERE
    nNum = #{nNum}

UPDATE
    kidslandAdminNotice
SET
    aCategory = #{aCategory}
  , aWriter = #{aWriter}
  , aTitle = #{aTitle}
  , aContent = #{aContent}
  , aDate = CURRENT_TIMESTAMP
WHERE
    aNum = #{aNum}
